    private void btn0ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn0ActionPerformed
        // TODO add your handling code here:
        if(bDot == false){
            if(dNum2 == 0 && sMinu.equals("")){
                txtLine.setText(btn0.getText());
            }else{
                txtLine.setText(txtLine.getText() + btn0.getText());
            }
        }else if(bDot == true){
            txtLine.setText(txtLine.getText() + btn0.getText());
        }
        dNum2 = Double.parseDouble(txtLine.getText());
    }//GEN-LAST:event_btn0ActionPerformed

