    private void btnBackSpaceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBackSpaceActionPerformed
        // TODO add your handling code here:
        String sTemp = "";
        char[] cLines = txtLine.getText().toCharArray();
        
        if(cLines.length > 0){
            if(cLines[cLines.length-1] == '.'){
                bDot = false;
            }
            
            for(int i = 0; i < cLines.length-1; i++){
                sTemp += cLines[i];
            }
            
            txtLine.setText(sTemp);
            dNum2 = Double.parseDouble(txtLine.getText());
        }
    }//GEN-LAST:event_btnBackSpaceActionPerformed

