    private void btnPMActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPMActionPerformed
        // TODO add your handling code here:
        if(dNum2 != 0){
            if((dNum2 - (int)dNum2) == 0){
                dNum2 *= -1;
                txtLine.setText(Integer.toString((int)dNum2));
            }else{
                dNum2 *= -1;
                txtLine.setText(Double.toString(dNum2));
            }
        }
        
        if(!(sOpr.equals(""))){  // 연산자를 누른 후
            if(dNum2 == 0){
                sMinu = "-";
                txtLine.setText(sMinu);
            }else{
                sMinu = "";
                txtLine.setText(sMinu);
            }
        }
    }//GEN-LAST:event_btnPMActionPerformed

    private void btnDotActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDotActionPerformed
        // TODO add your handling code here:
        if(bDot ==  true){
            return;
        }
        
        if(bDot == false && !txtLine.getText().equals("0.")){
            if(txtLine.getText().isEmpty()){
                txtLine.setText("0");
            }
            
            txtLine.setText(txtLine.getText() + ".");
            bDot = true;
        }else if(bDot == false && txtLine.getText().equals("0.")){
            dNum2 = Double.parseDouble(txtLine.getText());
            bDot = true;
        }
    }//GEN-LAST:event_btnDotActionPerformed

